targets.expr:
node_filesystem_size{fstype!~"rootfs|selinuxfs|autofs|rpc_pipefs|tmpfs", device !~ "overlay|/dev/mapper.*", mountpoint !~ "/boot.*", mountpoint =~ "/|/data"}
-
node_filesystem_free{fstype!~"rootfs|selinuxfs|autofs|rpc_pipefs|tmpfs", device !~ "overlay|/dev/mapper.*", mountpoint !~ "/boot.*", mountpoint =~ "/|/data"}

sum by (instance)(
  rate(redis_evicted_keys_total[5m])
)
/
clamp_min(
  sum by (instance)(
    rate(redis_expired_keys_total[5m])
  ),
  1
)

sum by (cluster)(
  mongodb_connpoolstats_connections_in_use
)
/
(
  sum by (cluster)(
    mongodb_connpoolstats_connections_in_use
  )
  +
  sum by (cluster)(
    mongodb_connpoolstats_connections_available
  )
)

topk(20,
  sum by (cluster, database, collection, type) (
    (rate(mongodb_mongod_top_time_seconds_total{
      database!~"^(admin|local|config)$",
      collection!~"^\\$cmd\\.",
      type!="Total"
    }[1m])) > 0
  )
)

topk(20,
  max by (cluster, database, collection, type) (
    (rate(mongodb_mongod_top_time_seconds_total{
      database!~"^(admin|local|config)$",
      type!="Total"
    }[1m])) > 0
  )
)

topk(20,
  sum by (cluster, database, collection, type) (
    (rate(mongodb_mongod_top_count_total{
      database!~"^(admin|local|config)$",
      collection!~"^\\$cmd\\.",
      type!="Total"
    }[1m])) > 0
  )
)

sum by (instance) (
  rate(mysql_global_status_commands_total{
    command="select"
  }[5m])
)
/
clamp_min(
  sum by (instance) (
    rate(mysql_global_status_commands_total{
      command=~"insert|update|delete|replace"
    }[5m])
  ),
  0.001
)
